def isValid(s):
	letters = {}
	auxConuter = -1
	oneChange =  True
	for i in s:
		if i in letters:
			letters[i] += 1
		else:
			letters[i] = 1
	for i in letters:
		if auxConuter == -1:
			auxConuter = letters[i]
		elif auxConuter != letters[i]:
			if oneChange and (auxConuter == letters[i] - 1 or letters[i] - 1 == 0):
				oneChange = False
				continue
			return 'NO'
	return 'YES'


print(isValid(''))